<div id="sustainability" class="tab-content">
    <div class="dashboard-grid">
        <div class="card"><div class="card-header"><div class="card-icon">🌱</div><h3 class="card-title">Sustainability Score</h3></div><div class="score-display"><div class="score-circle" id="sustainabilityScore">N/A</div><div class="score-label">Eco-Friendliness</div></div></div>
        <div class="card"><div class="card-header"><div class="card-icon">♻️</div><h3 class="card-title">Recyclability</h3></div><div class="score-display"><div class="score-circle" id="recyclabilityScore">N/A</div><div class="score-label">Recyclability Index</div></div></div>
        <div class="card"><div class="card-header"><div class="card-icon">🌿</div><h3 class="card-title">Biodegradability</h3></div><div class="score-display"><div class="score-circle" id="biodegradabilityScore">N/A</div><div class="score-label">Biodegradation</div></div></div>
    </div>
    <div class="card card-wide">
        <div class="card-header"><div class="card-icon">📊</div><h3 class="card-title">Sustainability Metrics Breakdown</h3></div>
        <div class="chart-container"><canvas id="sustainabilityChart"></canvas></div>
        <div class="status-grid">
            <div class="status-item"><div class="status-value" id="carbonFootprint">--</div><div class="status-label">Carbon Footprint</div></div>
            <div class="status-item"><div class="status-value" id="waterUsage">--</div><div class="status-label">Water Usage</div></div>
            <div class="status-item"><div class="status-value" id="chemicalImpact">--</div><div class="status-label">Chemical Impact</div></div>
            <div class="status-item"><div class="status-value" id="lifeCycleScore">--</div><div class="status-label">Life Cycle Score</div></div>
        </div>
    </div>
</div>
